import { useState } from 'react';

const ItemCount = ({stock, initial}) => {

    const [count, setCount] = useState(initial);


    const addCount = () => {
        if (count < (stock)) setCount(count++);
    }

    const removeCount = () => {
        if (count > (initial)) setCount(count--); 
    }

    const addToCart = () => {
        console.log("Se ha agregado productos, como pasar valores a otro componente." );
    }

    return (
        <div className="container">
            <div className="row">
                <div className="col-10 m-auto col-sm-3 text-center">
                    <div className="input-group">
                        <a className="btn btn-default btn-number" onClick={removeCount}>
                            <strong>-</strong>
                        </a>
                        <h1>{count}</h1>
                        <a className="btn btn-default btn-number" onClick={addCount}>
                            <strong>+</strong>
                        </a>
                    </div>
                    <br/>
                    <a type="button" className="btn btn-primary" onClick={addToCart}>Agregar al Carrito</a>
                </div>
            </div>
        </div>

    )
}
export default ItemCount;
