    //console.log(listaProductos);

    return (
        <div className="container bg-white">
            <div className="row">
            {cargando ? <p>Cargando...</p> : <ItemList listaProductos={listaProductos}/>}
            </div>

        </div>
    )
}
export default ItemListContainer;
