/*
    1.- Obtener el id que vino por param
    2.- voy a llamar al Endpoint para traer datos del usuario
    3.- mostrar los datos usando el componente UserDetails
*/

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getItem, prodData } from '../../data/datos'

export default function UserDetailsContainer () {

    const { idProducto } = useParams();
    const [detalleProducto, setProducto] = useState({});

    useEffect(()=>{
        setProducto([]);
        const producto = getItem(idProducto);
        //const productosProm = prodData(category);

        producto.then(
            (Datos)=>{
                setProducto(Datos);
            },
            (err)=>{
                console.log("error",err);
            }
        );

        console.log(detalleProducto);
       },[idProducto])

    return (
        <div>
            UserDetailsContainer
        </div>
    )
}
